import React, { useState } from "react";
import "../styles/SysDocs.css";

const SysDocsPage = () => {
  const [activeDoc, setActiveDoc] = useState("doc1");

  const docs = {
    doc1: "/path/to/doc1.pdf",
    doc2: "/path/to/doc2.pdf",
  };

  return (
    <div className="sys-container">
      {/* Left Sidebar */}
      <div className="sys-sidebar">
        <button
          className={`sys-btn ${activeDoc === "doc1" ? "active" : ""}`}
          onClick={() => setActiveDoc("doc1")}
        >
          Hub info
        </button>
        <button
          className={`sys-btn ${activeDoc === "doc2" ? "active" : ""}`}
          onClick={() => setActiveDoc("doc2")}
        >
          Essential Data
        </button>
        {/* Add more buttons */}
      </div>

      {/* Middle Content */}
      <div className="sys-content">
        <iframe
          src={docs[activeDoc]}
          title="Document Viewer"
          className="sys-doc-frame"
        ></iframe>
      </div>

      {/* Right Sidebar */}
      <div className="sys-links">
        <p className="sys-links-title">Important documents:</p>
        <ul>
          <li><a href="/path/to/doc1.pdf" download>Doc 1</a></li>
          <li><a href="/path/to/doc2.pdf" download>Doc 2</a></li>
        </ul>
      </div>
    </div>
  );
};

export default SysDocsPage;
