import React, { useState } from "react";
import "../styles/SysCreate.css";
import logo from "../assets/nexus.webp";
import dragIcon from "../assets/icons/stack.png"; // Replace with actual icon
import plusIcon from "../assets/icons/stack.png"; // Replace with actual icon

const SysCreatePage = () => {
  const [options, setOptions] = useState(["Option 1", "Option 2"]);

  const addOption = () => {
    setOptions([...options, `Option ${options.length + 1}`]);
  };

  return (
    <div className="syscreate-full-container">
      {/* Navbar */}
      {/* Left buttons same as before */}

      {/* Main Section */}
      <div className="syscreate-main-section">
        {/* Other Upload actions above */}

        {/* Poll Creation */}
        <div className="syscreate-poll-container">
          <span className="syscreate-poll-title">Create a poll</span>

          <p className="syscreate-poll-subtitle">Ask a question</p>

          <div className="syscreate-options">
            {options.map((option, index) => (
              <div key={index} className="syscreate-option-item">
                <input
                  type="text"
                  value={option}
                  onChange={(e) => {
                    const newOptions = [...options];
                    newOptions[index] = e.target.value;
                    setOptions(newOptions);
                  }}
                  className="syscreate-option-input"
                />
                <img src={dragIcon} alt="Drag" className="syscreate-drag-icon" />
              </div>
            ))}
            <button className="syscreate-add-option" onClick={addOption}>
              <img src={plusIcon} alt="Add" />
            </button>
          </div>

          <hr className="syscreate-divider" />

          <p className="syscreate-poll-subtitle">Set a deadline</p>
          <input className="syscreate-deadline-input" type="text" />

          <div className="syscreate-switches">
            <label className="switch">
              <input type="checkbox" />
              <span className="slider round"></span>
            </label>
            <span>Anonymous votes</span>

            <label className="switch">
              <input type="checkbox" />
              <span className="slider round"></span>
            </label>
            <span>Multiple answers</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SysCreatePage;
