import React, { useState } from "react";
import "../styles/SysPolls.css"; // You'll create this CSS based on SysForms.css
import logo from "../assets/nexus.webp";

const pollEntries = [
  {
    no: 1,
    type: "Group",
    date: "03-03-2025",
    status: "Done",
    question: "Question",
    options: ["Option 1", "Option 2", "Option 3"],
    selected: [true, true, false], // Example selected state
  },
  {
    no: 2,
    type: "Hub",
    date: "12-01-2025",
    status: "Not Done",
    question: "Question",
    options: ["Option 1", "Option 2", "Option 3"],
    selected: [false, false, false],
  },
];

const SysPollsPage = () => {
  const [polls, setPolls] = useState(pollEntries);

  const toggleOption = (pollIndex, optionIndex) => {
    const newPolls = [...polls];
    newPolls[pollIndex].selected[optionIndex] = !newPolls[pollIndex].selected[optionIndex];
    setPolls(newPolls);
  };

  return (
    <div className="syspolls-full-container">
      {/* Navbar */}
      <div className="syspolls-navbar">
        <img src={logo} alt="Nexus Logo" className="syspolls-navbar-logo" />
        <div className="syspolls-nav-links">
          <span>My account</span>
          <span>Messages</span>
        </div>
      </div>

      {/* Content */}
      <div className="syspolls-content-section">
        {/* Left Buttons */}
        <div className="syspolls-left-buttons">
          <button className="syspolls-button" style={{ color: "black" }}>Hub info</button>
          <button className="syspolls-button" style={{ color: "black" }}>Essential data</button>
          <button className="syspolls-button" style={{ color: "black" }}>Manage and create</button>
          <button className="syspolls-button" style={{ color: "black" }}>Key Information</button>
          <button className="syspolls-button" style={{ color: "black" }}>Essential Documents</button>
          <button className="syspolls-button" style={{ color: "black" }}>Forms</button>
          <button className="syspolls-button active" style={{ color: "#8a4ddf" }}>Polls</button>
        </div>

        {/* Polls Section */}
        <div className="syspolls-polls-section">
          <h2 className="syspolls-title">Ongoing polls</h2>
          <table className="syspolls-table">
            <thead>
              <tr>
                <th>No.</th>
                <th>Type</th>
                <th>Date</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {polls.map((poll, pollIndex) => (
                <tr key={pollIndex}>
                  <td>{poll.no}</td>
                  <td>{poll.type}</td>
                  <td>{poll.date}</td>
                  <td className={poll.status === "Done" ? "done" : "not-done"}>{poll.status}</td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Poll Questions */}
          {polls.map((poll, pollIndex) => (
            <div key={pollIndex} className="syspolls-poll-card">
              <h4>{poll.no < 10 ? `0${poll.no}.` : `${poll.no}.`}</h4>
              <h5>{poll.question}</h5>
              {poll.options.map((option, optionIndex) => (
                <label key={optionIndex} className="syspolls-option">
                  <input
                    type="checkbox"
                    checked={poll.selected[optionIndex]}
                    onChange={() => toggleOption(pollIndex, optionIndex)}
                  />
                  {option}
                </label>
              ))}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SysPollsPage;
