import React, { useState } from "react";
import "../styles/SysInfo.css";
import logo from "../assets/nexus.webp";

const articles = [
  {
    title: "Anunț privind acordarea burselor finale și contestații 2024/2025",
    content: `Anunțul privind acordarea burselor finale și contestațiile pentru semestrul I al anului universitar 2024/2025...`
  },
  {
    title: "Regulament intern actualizat",
    content: `Regulamentul intern a fost actualizat începând cu data de 01.04.2025...`
  },
];

const SysInfoPage = () => {
  const [selectedArticle, setSelectedArticle] = useState(articles[0]);

  return (
    <div className="sysinfo-full-container">
      {/* Navbar */}
      <div className="sysinfo-navbar">
        <img src={logo} alt="Nexus Logo" className="sysinfo-navbar-logo" />
        <div className="sysinfo-nav-links">
          <span>My account</span>
          <span>Messages</span>
        </div>
      </div>

      {/* Main Content */}
      <div className="sysinfo-content-section">
        {/* Left Buttons */}
        <div className="sysinfo-left-buttons">
          <button className="sysinfo-button" style={{ color: "black" }}>Hub info</button>
          <button className="sysinfo-button" style={{ color: "black" }}>Essential data</button>
          <button className="sysinfo-button" style={{ color: "black" }}>Manage and create</button>
          <button className="sysinfo-button" style={{ color: "black" }}>Essential Documents</button>
          <button className="sysinfo-button active" style={{ color: "#8a4ddf" }}>Key Information</button>
          <button className="sysinfo-button" style={{ color: "black" }}>Forms</button>
          <button className="sysinfo-button" style={{ color: "black" }}>Polls</button>
        </div>

        {/* Middle Viewer */}
        <div className="sysinfo-middle-viewer">
          <h2 className="sysinfo-article-title">{selectedArticle.title}</h2>
          <div className="sysinfo-article-content">
            <p>{selectedArticle.content}</p>
          </div>
        </div>

        {/* Right Info List */}
        <div className="sysinfo-right-info">
          <div className="sysinfo-right-title">
            <span className="sysinfo-black">Important </span>
            <div className="sysinfo-purple-words">
              <span>data</span>
              <span>information</span>
              <span>articles</span>
            </div>
            <div className="sysinfo-divider"></div>
            <span className="sysinfo-black">to be aware of</span>
          </div>
          {articles.map((art, index) => (
            <p
              key={index}
              className="sysinfo-article-link"
              onClick={() => setSelectedArticle(art)}
            >
              {art.title}
            </p>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SysInfoPage;
